// Task.js
import React, { useState } from 'react';
import Modal from './Modal'; // Import the Modal component
import './Task.css'; // Create and import a CSS file for styling if needed

const Task = ({ id, title, description, dueDate, priority, status, fileUrl }) => {
    const [isModalOpen, setModalOpen] = useState(false);

    const toggleModal = () => {
        setModalOpen(!isModalOpen);
    };
        return (
        <div className="task">
            <h3>{title}</h3>
            <p>{description}</p>
            <p>Due Date: {dueDate}</p>
            <p>Priority: {priority}</p>
            <p>Status: {status}</p>
            {fileUrl && (
                <button onClick={toggleModal} className="view-attachment-btn">
                    View Attachment
                </button>
            )}

            <Modal isOpen={isModalOpen} onClose={toggleModal}>
                {/* Content you want to show in the modal, e.g., an image or document */}
                <iframe 
        src={fileUrl} 
        title="Attachment" 
        className="attachment-iframe"
        allowFullScreen  // Optional: if you want to allow full-screen mode
    />            </Modal>
        </div>
    );
};

export default Task;
