// Task.js
import React from 'react';
import './Task.css'; // Create and import a CSS file for styling if needed

const Task = ({ id, title, description, dueDate, priority, status }) => {
    return (
        <div className="task">
            <h3>{title}</h3>
            <p>{description}</p>
            <p>Due Date: {dueDate}</p>
            <p>Priority: {priority}</p>
            <p>Status: {status}</p>
        </div>
    );
};

export default Task;
