// Task.js
import React, { useState } from "react";
import Modal from "./Modal";
import "./Task.css";

const Task = ({
  id,
  title,
  description,
  dueDate,
  priority,
  status,
  fileUrl,
}) => {
  const [isModalOpen, setModalOpen] = useState(false);

  const toggleModal = () => {
    setModalOpen(!isModalOpen);
  };

  const handleDelete = () => {
    fetch(`http://localhost:3001/task/${id}`, {
      method: 'DELETE',
      credentials: 'include'
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      // Optionally, refresh the tasks list or redirect
      window.location.reload(); // This is a simple way to refresh the tasks
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  return (
    <div className="task">
      {/* ... existing content ... */}
      {fileUrl && (
        <button onClick={toggleModal} className="view-attachment-btn">
          View Attachment
        </button>
      )}
      <button onClick={handleDelete} className="task-done-btn">
        Task Done
      </button>

      <Modal isOpen={isModalOpen} onClose={toggleModal}>
        {/* ... existing modal content ... */}
      </Modal>
    </div>
  );
};

export default Task;
