import React, { useState } from 'react';
import './AddTaskForm.css'; // Import the CSS file for styling

const AddTaskForm = () => {
    const [task, setTask] = useState({
        title: '',
        description: '',
        dueDate: '',
        priority: '',
    });

    const handleChange = (e) => {
        setTask({ ...task, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        fetch('http://localhost:3001/task', {
            method: 'POST',
            credentials: 'include', // Ensures credentials/cookies are sent with the request
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(task), // Convert the React state to JSON and send it as the POST body
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            console.log('Task added:', data);
            // Handle successful addition of task here (e.g., redirecting or clearing the form)
        })
        .catch(error => {
            console.error('Error:', error);
            // Handle error here (e.g., showing error message)
        });
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    name="title" 
                    className="form-input" // Added class for input
                    value={task.title} 
                    onChange={handleChange} 
                    placeholder="Title"
                />
                <textarea
                    name="description"
                    className="form-input" // Added class for textarea
                    value={task.description}
                    onChange={handleChange}
                    placeholder="Description"
                />
                <input 
                    type="date" 
                    name="dueDate" 
                    className="form-input" // Added class for input
                    value={task.dueDate} 
                    onChange={handleChange} 
                />
                <select
                    name="priority"
                    className="form-input" // Added class for select
                    value={task.priority}
                    onChange={handleChange}
                >
                    <option value="">Select Priority</option>
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                </select>
                <button type="submit" className="form-button">Add Task</button> {/* Updated class for button */}
            </form>
        </div>
    );
};

export default AddTaskForm;
