import React, { useState, useEffect } from 'react';
import Task from './Task';
import './TaskList.css'; // Import the CSS file

const TaskList = () => {
    const [tasks, setTasks] = useState([]);
    const handleLogin = () => {
        window.location.href = `http://localhost:3000/add`;
    };

    const handleLogout = () => {
        const auth2 = window.gapi.auth2.getAuthInstance();
        if (auth2 != null) {
            auth2.signOut().then(auth2.disconnect().then(() => console.log('LOGOUT SUCCESSFUL')));
        }
    };


    useEffect(() => {
        fetch('http://localhost:3001/tasks', { credentials: 'include' })
            .then(response => response.json())
            .then(data => setTasks(data))
            .catch(error => console.error('Error:', error));
    }, []);

    return (
        <div className="container">
            <button className="logout-button" onClick={handleLogout}>Logout</button>
            <h2 className="title">My Tasks</h2>
            <div className="task-list">
                {tasks.length > 0 ? (
                    tasks.map(task => <Task key={task.id} {...task} />)
                ) : (
                    <p className="no-tasks">No tasks found</p>
                )}
            </div>
            <button className="add-task-button" onClick={handleLogin}>Add Task</button>
        </div>
    );
};

export default TaskList;
